
public class MainClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello World");
		System.out.println("Hello World");
		System.out.println("Hello World");
		System.out.println("Hello World");
		System.out.print("하이");
		System.out.print("바이");
		
//		printf --> format
		System.out.printf("%d %s", 123,"성공을 기원합니다.");
		System.out.println();
		
		// escape sequence
		// \n, \t, \", \', \b, \\
		
		//\n
		System.out.print("나는 끝까지 노력할 것이다\n");
		System.out.print("성공 할\n 때 까지\n");
		
		//\t
		System.out.println("홍길동\t 24 서울시");
		System.out.println("일지\t 22 부산시");
		System.out.println("일지렁이\t 42 부산시");
		
		//\"
		System.out.println("\"나는 문제 없어\"");
		System.out.println("\"나는 \'문\'제 없어\"");
		System.out.println("\\나는 문제 없어\\");
		
	}
	/*
		범위 주석문 : System.out.println("가지마 가지마 가지마")
	 */
	
}
